mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
    return mat2(
        c, s,
        -s, c
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

       float time = iTime;
       float alpha = 1.0;
       float orbitals = 10.;
       vec3 color = vec3(0.1, 0.5, 0.8);
       float radius = .25;
        vec3 shape = vec3(0.);
        float expand = .5555;


        float f;



         vec2 xUv = ( fragCoord -.5*iResolution.xy )/iResolution.y;


        for(float i = 0.0; i < orbitals; i++){

            // based off https://www.shadertoy.com/view/NscyDN
            float s = sin((time / 111.0) + i * cos(time*0.02+i)*0.05+0.05);
            float c = cos((time / 111.0) + i * sin(time*0.02+i)*0.05+0.05);
            c += sin(time) *  expand; //not sure if i like this 
            s += sin(time) * expand; //not sure if i like this 
            // Point manipulations based on position, time, and index
            vec2 pt = vec2(c, s);
            f = 0.01 / abs(length(xUv / pt) - radius);
            float idx = float(i) / orbitals;
            idx = fract(idx);
            shape += f * color;
            xUv *= r2d(time*0.1);

        }


        vec4 atom = vec4(shape.r, shape.g, shape.b, 1.0);
        fragColor = vec4(atom) * vec4(alpha);

}
